mapper.select:
	SELECT  FROM pos_sku_base a
	LEFT JOIN  pos_whs_butype b ON b.code=a.BU_TYPE


            AND a.ID = #{id, jdbcType=VARCHAR}


            AND a.BAR_CODE = #{barCode, jdbcType=VARCHAR}


            AND a.CODE = #{code, jdbcType=VARCHAR}


            AND a.EXT_CODE1 = #{extCode1, jdbcType=VARCHAR}


            AND a.EXT_CODE2 = #{extCode2, jdbcType=VARCHAR}


            AND a.EXT_CODE3 = #{extCode3, jdbcType=VARCHAR}


            AND a.JM_CODE = #{jmCode, jdbcType=VARCHAR}


            AND a.KEY_PROPERTIES = #{keyProperties, jdbcType=VARCHAR}


            AND a.NAME = #{name, jdbcType=VARCHAR}


            AND a.SUPPLIER_CODE = #{supplierCode, jdbcType=VARCHAR}


            AND a.VERSION = #{version, jdbcType=VARCHAR}


            AND a.BRAND_ID = #{brandId, jdbcType=VARCHAR}


            AND a.PRODUCT_ID = #{productId, jdbcType=VARCHAR}


            AND a.EN_NAME = #{enName, jdbcType=VARCHAR}


            AND a.COLOR = #{color, jdbcType=VARCHAR}


            AND a.COLOR_NAME = #{colorName, jdbcType=VARCHAR}


            AND a.SKU_SIZE = #{skuSize, jdbcType=VARCHAR}


            AND a.PAPER_SKU_ID = #{paperSkuId, jdbcType=VARCHAR}


            AND a.EXT_PROP1 = #{extProp1, jdbcType=VARCHAR}


            AND a.EXT_PROP2 = #{extProp2, jdbcType=VARCHAR}


            AND a.EXT_PROP3 = #{extProp3, jdbcType=VARCHAR}


            AND a.EXT_PROP4 = #{extProp4, jdbcType=VARCHAR}


            AND a.EXT_PROP5 = #{extProp5, jdbcType=VARCHAR}


            AND a.WARRANTY_CARD_TYPE = #{warrantyCardType, jdbcType=VARCHAR}


            AND a.CUSTOMER_ID = #{customerId, jdbcType=VARCHAR}


            AND a.WIDTH = #{width, jdbcType=VARCHAR}


            AND a.HEIGHT = #{height, jdbcType=VARCHAR}


            AND a.LENGTH = #{length, jdbcType=VARCHAR}


            AND a.IS_SN_SKU = #{isSnSku, jdbcType=VARCHAR}


            AND a.SALES_MODEL = #{salesModel, jdbcType=VARCHAR}


            AND a.GROSS_WEIGHT = #{grossWeight, jdbcType=VARCHAR}


            AND a.BOX_QTY = #{boxQty, jdbcType=VARCHAR}


            AND a.STORE_MODE = #{storeMode, jdbcType=VARCHAR}


            AND a.IS_GIFT = #{isGift, jdbcType=VARCHAR}


            AND a.IS_RAILWAY = #{isRailway, jdbcType=VARCHAR}


            AND a.SKU_CATEGORIES_ID = #{skuCategoriesId, jdbcType=VARCHAR}


            AND a.CUSTOMER_SKU_CODE = #{customerSkuCode, jdbcType=VARCHAR}


            AND a.WARNING_DATE = #{warningDate, jdbcType=VARCHAR}


            AND a.VALID_DATE = #{validDate, jdbcType=VARCHAR}


            AND a.TIME_TYPE = #{timeType, jdbcType=VARCHAR}


            AND a.WARNING_DATE_LV1 = #{warningDateLv1, jdbcType=VARCHAR}


            AND a.WARNING_DATE_LV2 = #{warningDateLv2, jdbcType=VARCHAR}


            AND a.LIST_PRICE = #{listPrice, jdbcType=VARCHAR}


            AND a.INTERFACE_TYPE = #{interfaceType, jdbcType=VARCHAR}


            AND a.SN_TYPE = #{snType, jdbcType=VARCHAR}


            AND a.SN_CHECK_MODE = #{snCheckMode, jdbcType=VARCHAR}


            AND a.COUNTRY_OF_ORIGIN = #{countryOfOrigin, jdbcType=VARCHAR}


            AND a.SP_TYPE = #{spType, jdbcType=VARCHAR}


            AND a.CATEGORY = #{category, jdbcType=VARCHAR}


            AND a.SKU_TYPE_ID = #{skuTypeId, jdbcType=VARCHAR}


            AND a.BU = #{bu, jdbcType=VARCHAR}


            AND a.UNIT_NAME = #{unitName, jdbcType=VARCHAR}


            AND a.HTS_CODE = #{htsCode, jdbcType=VARCHAR}


            AND a.CUSTOMER_AND_EXT = #{customerAndExt, jdbcType=VARCHAR}


            AND a.USER_ID = #{userId, jdbcType=VARCHAR}


            AND a.THREEDIMENSIONA_LAST_TIME = #{threedimensionaLastTime, jdbcType=VARCHAR}


            AND a.CHILD_SN_QTY = #{childSnQty, jdbcType=VARCHAR}


            AND a.BU_TYPE = #{buType, jdbcType=VARCHAR}
